import semweb
import gRefine
import re
@remus.splitter
def testfunc( handle ):
	ref = gRefine.Refine( "http://localhost:3333" )
	projects = ref.projectList()
	for projID in projects:
		name = re.split( "_", projects[projID]["name"] )[1]
		remus.emit( projID, name )

html.remus_mapper:
import remus
import re
import gRefine
reTab = re.compile(r'\t')
@remus.mapper
def testfunc( key, val ):
	ref = gRefine.Refine( "http://localhost:3333" )
	handle = ref.getTSV( key )
	header = None	
	for line in handle:
		tmp = reTab.split( line.rstrip() )
		if header is None:
			header = []
			for name in tmp:
				if name.startswith( val ):
					header.append( name )
				else:
					header.append( "%s_%s" % (val, name) )
		else:
			out = {}
			for i in range(1, len(tmp)):
				if ( tmp[i] != "null" ):
					out[ header[i] ] = tmp[i]
			remus.emit( tmp[0], out )
	handle.close()

