html.remus_mapper:
import remus
import re
import gRefine
reTab = re.compile(r'\t')
@remus.mapper
def testfunc( key, val ):
	ref = gRefine.Refine( "http://localhost:3333" )
	handle = ref.getTSV( key )
	header = None	
	for line in handle:
		tmp = reTab.split( line.rstrip() )
		if header is None:
			header = []
			for name in tmp:
				if name.startswith( val ):
					header.append( name )
				else:
					header.append( "%s_%s" % (val, name) )
		else:
			out = {}
			for i in range(1, len(tmp)):
				if ( tmp[i] != "null" ):
					out[ header[i] ] = tmp[i]
			if ( len(out) ):
				remus.emit( tmp[0], out )
	handle.close()

html.remus_reducer:
import remus
import re
reSample = re.compile(r'^(....\-..\-....)\-(...)')
@remus.reducer
def barcode_remap( key, vals ):
	res = reSample.search( key )
	if res:
		out = { key : [] }
		for val in vals:
			out[key].append( val )
		remus.emit( res.group(1), out )
	else:
		out = { key : [] }
		for val in vals:
			out[key].append( val )
		remus.emit( key, out )

import remus
import re
reSample = re.compile(r'^(....\-..\-....)\-(...)')
@remus.reducer
def sample_reduce( key, vals ):
	outmap = {}
	for sampleMap in vals:
		for valKey in sampleMap:
			if not outmap.has_key( valKey ):
				outmap[ valKey ] = []
			outmap[ valKey ].append( sampleMap[ valKey ] )
	remus.emit( key, outmap )

html.remus_mapper:
import remus
@remus.mapper
def col_map( key, vals ):
	for sampleKey in vals: 
		for sampleVal in vals[ sampleKey ]:
			remus.emit( sampleKey, sampleVal )

html.remus_reducer:
import remus
@remus.reducer
def col_map( key, vals ):
	out = {}
	for val in vals:
		if not out.has_key( val ):
			out[ val ] = len(out)
	remus.emit( key, out )
