remus_pipeline.remus_split:
@remus.splitter
def split( info ):
	handle = remus.open( "test.file" )
	for line in handle:
		tmp = line.rstrip().split("\t")
		print tmp
		remus.emit( tmp[0], tmp[1] )
	handle.close()

remus_pipeline.remus_map:
@remus.mapper
def map( key, val ):
	for i in range(int(val)):
		remus.emit( key, i )

remus_pipeline.remus_reduce:
@remus.reducer
def reduce( key, vals ):
	remus.emit( key, len(list(vals)) )

remus_pipeline.remus_merge:
@remus.merger
def reduce( lkey, lvals, rkey, rvals ):
	word = None
	for val in lvals:
		word = val
	count = None
	for val in rvals:
		count = val
	remus.emit( "%s_%s" % (lkey,rkey), [word] * count )

remus_pipeline.remus_pipe:
@remus.pipe
def reduce( handles ):
	for key, value in handles[0]:
		remus.emit( "%s_%s" % (key, key), value * value )

remus_pipeline.remus_match:
@remus.matcher
def reduce( key, lvals, rvals ):
	sum = 0
	for valList in lvals:
		for val in valList:
			sum += int(val)
	square = None
	for val in rvals:
		square = val
	if sum == square:
		remus.emit( key, "GOOD" )
	else:
		remus.emit( key, "BAD" )
